package Relationship;public class Car {    private String color = null;    private int maxSpeed = 0;    public Car(){        color = null;        maxSpeed = 0;    }    public void setColor(String color) {        this.color = color;    }    public final String getColor(){        return color;    }    public void setMaxSpeed(int maxSpeed) {        this.maxSpeed = maxSpeed;    }    public final int getMaxSpeed() {        return maxSpeed;    }    public void carInfo(){        System.out.println("Car color: "+color);        System.out.println("Car max speed: "+maxSpeed);    }}